var show = true;
var checkboxes = document.getElementById("checkBoxes");

function addCountries() {
    checkboxes.innerHTML = "<option>Select Countries</option>";
    for (i = 0; i < countries.length; i++) {
        checkboxes.innerHTML += '<option><label id='+i+'><input type="checkbox"/>' + countries[i].name + '</label></option>';
    }
}

function showCheckboxes() {
    addCountries();
    if (show) {
        checkboxes.style.display = "block";
        show = false;
    } else {
        checkboxes.style.display = "none";
        show = true;
    }
}

